def countScore(seq1, seq2):
    """Count the number of point mutations between sequence 1 and 2"""
    score = 0
    for i in range(len(seq1)):
        if seq1[i] != seq2[i]:
            score +=1
    return score


if __name__ == "__main__":
    seq1 = ""
    seq2 = ""
    score = countScore(seq1, seq2)
    print(score)
